mapper.select:
select

from txn_record
where 1 = 1

    and account_number = #{accountNumber,jdbcType=VARCHAR}


    and description = #{description,jdbcType=VARCHAR}


    and customer_id = #{customerId,jdbcType=VARCHAR}

limit #{start}, #{size}

select
count(1)
from txn_record
where 1 = 1

    and account_number = #{accountNumber,jdbcType=VARCHAR}


    and description = #{description,jdbcType=VARCHAR}


    and customer_id = #{customerId,jdbcType=VARCHAR}
